function mascara(e,n){v_obj=e,v_fun=n,setTimeout("execmascara()",1)}function execmascara(){v_obj.value=v_fun(v_obj.value)}function mtel(e){return e=e.replace(/\D/g,""),e=e.replace(/^(\d{2})(\d)/g,"($1) $2"),e=e.replace(/(\d)(\d{4})$/,"$1-$2")}function getByclass(e){return document.getElementsByClassName(e)}window.onload=function(){for(var e=document.getElementsByClassName("input--phone"),n=0;n<e.length;n++)e[n].onkeypress=function(){mascara(this,mtel)}};